import React from 'react'

const ContactUs = () => {
  return (
    <>
    
    
    <style
    dangerouslySetInnerHTML={{
      __html:
        '
        .contact .info-wrap {
            box-shadow: 0px 2px 15px rgba(0, 0, 0, 0.1);
            padding: 30px;
        }

        .contact .info {
            background: #fff;
        }

        .contact .info i {
            font-size: 20px;
            color: #172A3A;
            float: left;
            width: 44px;
            height: 44px;
            border: 1px solid #172A3A;
            display: flex;
            justify-content: center;
            align-items: center;
            border-radius: 50px;
            transition: all 0.3s;
        }

        .contact .info h4 {
            padding: 0 0 0 60px;
            font-size: 22px;
            font-weight: 600;
            margin-bottom: 5px;
            color: #111;
        }

        .contact .info p {
            padding: 0 0 0 60px;
            margin-bottom: 0;
            font-size: 14px;
            color: #444444;
        }

        .contact .info:hover i {
            background: #172A3A;
            color: #fff;
        }

        .contact .email-form {
            width: 100%;
            box-shadow: 0 0 24px 0 rgba(0, 0, 0, 0.12);
            padding: 30px;
            background: #fff;
        }

        .contact .email-form .form-group {
            padding-bottom: 8px;
        }

        .contact .email-form .error-message {
            display: none;
            color: #fff;
            background: #bb0303;
            text-align: left;
            padding: 15px;
            font-weight: 600;
        }

        .contact .email-form .error-message br+br {
            margin-top: 25px;
        }

        .contact .email-form .sent-message {
            display: none;
            color: #fff;
            background: #004346;
            text-align: center;
            padding: 15px;
            font-weight: 600;
        }

        .contact .email-form .loading {
            display: none;
            background: #fff;
            text-align: center;
            padding: 15px;
        }

        .contact .email-form .loading:before {
            content: "";
            display: inline-block;
            border-radius: 50%;
            width: 24px;
            height: 24px;
            margin: 0 10px -6px 0;
            border: 3px solid #004346;
            border-top-color: #eee;
            -webkit-animation: animate-loading 1s linear infinite;
            animation: animate-loading 1s linear infinite;
        }

        .contact .email-form input,
        .contact .email-form textarea {
            border-radius: 0;
            box-shadow: none;
            font-size: 14px;
            border-radius: 4px;
        }

        .contact .email-form input:focus,
        .contact .email-form textarea:focus {
            border-color: #172A3A;
        }

        .contact .email-form input {
            height: 44px;
        }

        .contact .email-form textarea {
            padding: 10px 12px;
        }

        .contact .email-form button[type=submit] {
            background: #172A3A;
            border: 0;
            padding: 10px 24px;
            color: #fff;
            transition: 0.4s;
            border-radius: 4px;
        }



        .contact .email-form button[type=submit]:hover {
             background: #004346;
        }

        .col-lg-10 {
            padding-top: 20px;
        }
    '
    }}
  />
  <section id="contact" className="contact">
    <div className="container">
      {/* onsubmit="readForm()" */}
      <div className="row mt-5 justify-content-center" data-aos="fade-up">
        <div className="col-lg-10">
          <form
            action="#"
            method="post"
            role="form"
            id="contactForm"
            className="email-form"
          >
            <div className="row">
              <div className="col-md-6 form-group">
                <input
                  type="text"
                  name="name"
                  className="form-control"
                  id="name"
                  placeholder="Your Name"
                  required=""
                />
              </div>
              <div className="col-md-6 form-group mt-3 mt-md-0">
                <input
                  type="email"
                  className="form-control"
                  name="email"
                  id="email"
                  placeholder="Your Email"
                  required=""
                />
              </div>
            </div>
            <div className="form-group mt-3">
              <input
                type="text"
                className="form-control"
                name="subject"
                id="subject"
                placeholder="Subject"
                required=""
              />
            </div>
            <div className="form-group mt-3">
              <textarea
                className="form-control"
                name="message"
                id="message"
                rows={5}
                placeholder="Message"
                required=""
                defaultValue={""}
              />
            </div>
            <div className="text-center">
              <button id="mySubmitBtn" type="submit" />
              Send Message
            </div>
          </form>
        </div>
      </div>
    </div>
  </section>
    
    
    
    
    
    
    
    </>
  )
}

export default ContactUs
